def get_points_for_mars_circumference(radius, num_points=100):
    # radius = meters, num_points = number of points to generate
    angles = np.linspace(np.pi / 2, -1.5 * np.pi, num_points)  # Start at the top and go clockwise
    x = (np.round(radius * np.cos(angles) / 100)/10)
    y = (np.round(radius * np.sin(angles) / 100)/10)
    # return as copy-pastable numpy arrays strings
    return f"np.array({x.tolist()}) \n\n\n\n\n\n\n\n\n np.array({y.tolist()})"

